mapper.update:
UPDATE wp_strate_single_sum
`start_time` = #{startTime}, `end_time` = #{endTime}, `update_time` = now()
WHERE user_id = #{userId} and type = #{type} and pool_type = #{poolType}

UPDATE wp_strate_single_sum
`end_time` = date_add(end_time,interval #{daySum} day), `update_time` = now()
WHERE user_id = #{userId} and type = #{type} and pool_type = #{poolType}

UPDATE wp_strate_single_sum
`end_time` = date_add(end_time,interval #{daySum} day), `update_time` = now()
WHERE user_id = #{userId} and type = #{type}

UPDATE wp_strate_single_sum
`start_time` = #{startTime}, `end_time` = #{endTime}, `update_time` = now()
WHERE user_id = #{userId} and type = #{type}

update.foreach:
update wp_strate_single_sum t

    t.update_time = sysdate(),
    t.start_time = #{item.startTime},
    t.end_time = #{item.endTime}

where  t.user_id = #{item.userId} and t.type = #{item.type} and t.pool_type = #{item.poolType}
